import React, { useState } from 'react'

const Form = () => {

  const [contactForm, setContactForm] = useState({name: '', email: '', comment: ''})
  const [formErrors, setFormErrors] = useState ({})

  const validate = (values) => {
    const errors = {}
    const regEx = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
    if (regEx.test(email))

    if(!values.name) 
      errors.name = "You must enter a name."

    if(!values.email) 
      errors.email = "You must enter an e-mail address."
    else if(!regEx.test(values.email))
      

    if(!values.comment) 
      errors.comment = "You must enter a comment."

    return errors;
  }
  
  const handleChange = event => {
    const {id, value} = event.target
    setContactForm({...contactForm, [id]: value})
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    setFormErrors(validate(contactForm))
  }
  
  return (
    <section className="contact-form">
      <div className="container">
        <h2>Come in Contact with Us</h2>
        <pre>{JSON.stringify(formErrors)}</pre>
        <form onSubmit={handleSubmit} novalidate>
          <div>
            <input id="name" type="text" placeholder="Name" class="form-control" value={contactForm.name} onChange={handleChange}/>
            <div className="errorMessage">{formErrors.name}</div>
          </div>
          <div>
            <input id="email" type="email" placeholder="Your E-mail" class="form-control" value={contactForm.email} onChange={handleChange}/>
            <div className="errorMessage">{formErrors.email}</div>
          </div>
          <div className="textarea">
            <textarea id="comment" cols="30" rows="10" placeholder="Comments" class="form-control" value={contactForm.comment} onChange={handleChange}></textarea>
            <div className="errorMessage">{formErrors.comment}</div>
          </div>
          <div>
            <button type="submit" class="btn btn-main">Post Comments</button>
          </div>      
        </form>
      </div>
    </section>
  )
}

export default Form
