import React, { useState } from 'react'

const Form = () => {
  const [contactForm, setContactForm] = useState({name: '', email: '', comment: ''})
  const [formErrors, setFormErrors] = useState ({})

  const validate = (values) => {
    const errors = {}
    const regEx = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/

    if(!values.name) 
      errors.name = "You must enter a name."
    else if (values.name.length < 2)
      errors.name = "Your name must be longer than two letters."

    if(!values.email) 
      errors.email = "You must enter an e-mail address."
    else if(!regEx.test(values.email))
      errors.email = "Your e-mail address must be valid eg (example@domain.com)"

    if(!values.comment) 
      errors.comment = "You must enter a comment."
    else if (values.comment.length < 10)
      errors.comment = "Your comment must be longer than 10 characters."

    if(Object.keys(errors).length === 0)
      setCanSubmit(true)
    else
      setCanSubmit(false)

    return errors;
  }

  
  const handleChange = event => {
    const {id, value} = event.target
    setContactForm({...contactForm, [id]: value})
  }

  const handleSubmit = (event) => {
    event.preventDefault()
    setFormErrors(validate(contactForm))
  }

  const handleKeyUp = (event) => { 
    event.preventDefault()
    setFormErrors(validate(contactForm))
  }
  
  return (
    <section className="contact-form">
      <div className="container">
        <h2>Come in Contact with Us</h2>
          setCanSubmit ? ()
      </div>
    </section>
  )
}

export default Form
